clc A=[-5 -2; 2 -2]; [P,val]=eig(A); t=1; f=sqrt(val(1,1)); s=sqrt(val(2,2)); c=zeros(1,1); d=ones(1,1); B=[exp(f*t)  exp(-f*t) c c; c c exp(s*t) exp(-s*t);f*exp(f*t) (-f)*exp(-f*t)  c c; c c s*exp(s*t) (-s)*exp(-s*t)]; Y=[c c c c; c c c c; d d d d; d d d d]; coef=inv(B)*Y; t2=0:0.01:10; y1=coef(1,4)*exp(f*t2)+coef(2,4)*exp(-f*t2); y2=coef(3,4)*exp(s*t2)+coef(4,4)*exp(-s*t2); plot(t2,y1,'*',t2,y2,'+r'); ylim([2,4]);xlabel('time');ylabel('displacement');legend(['y1'],['y2']);  